// App.tsx
import React, { useState } from 'react';
import './App.css';

interface Nota {
  id: number;
  titulo: string;
  conteudo: string;
}

const App: React.FC = () => {
  const [notas, setNotas] = useState<Nota[]>([]);
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState('');

  const adicionarNota = () => {
    if (!titulo || !conteudo) return;
    const novaNota: Nota = {
      id: new Date().getTime(),
      titulo,
      conteudo,
    };
    setNotas([...notas, novaNota]);
    setTitulo('');
    setConteudo('');
  };

  const excluirNota = (id: number) => {
    const novasNotas = notas.filter((nota) => nota.id !== id);
    setNotas(novasNotas);
  };

  return (
    <div className="App">
      <h1>Bloco de Notas</h1>
      <div className="form">
        <input
          type="text"
          placeholder="Título da Nota"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <textarea
          placeholder="Conteúdo da Nota"
          value={conteudo}
          onChange={(e) => setConteudo(e.target.value)}
        ></textarea>
        <button onClick={adicionarNota}>Adicionar Nota</button>
      </div>
      <div className="notas">
        {notas.map((nota) => (
          <div key={nota.id} className="nota">
            <h2>{nota.titulo}</h2>
            <p>{nota.conteudo}</p>
            <button onClick={() => excluirNota(nota.id)}>Excluir Nota</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
